import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const BookingManager = () => {
  const { id } = useParams();
  const [bookings, setBookings] = useState([]);
  const [availableStays, setAvailableStays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    stay: id || "",
    checkInDate: "",
    checkOutDate: "",
  });
  const currentUserId = localStorage.getItem("userId");

  useEffect(() => {
    if (id) {
      setFormData((prev) => ({ ...prev, stay: id }));
    }
    fetchBookings();
    fetchStays();
  }, [id]);

  const fetchBookings = async () => {
    setLoading(true);
    try {
      const res = await axios.get("http://localhost:5003/api/bookings");
      setBookings(res.data || []);
    } catch (err) {
      console.error("Error fetching bookings:", err);
      setError("Failed to fetch bookings");
    } finally {
      setLoading(false);
    }
  };

  const fetchStays = async () => {
    try {
      const res = await axios.get("http://localhost:5003/api/stays/stay");
      if (Array.isArray(res.data)) {
        setAvailableStays(res.data);
      } else {
        throw new Error("Invalid stays data");
      }
    } catch (err) {
      console.error("Error fetching stays:", err);
      setError("Failed to fetch stays");
    }
  };

  const calculateNights = (checkIn, checkOut) => {
    const inDate = new Date(checkIn);
    const outDate = new Date(checkOut);
    return Math.max(0, Math.round((outDate - inDate) / (1000 * 60 * 60 * 24)));
  };

  const calculateTotalAmount = () => {
    const nights = calculateNights(formData.checkInDate, formData.checkOutDate);
    const selectedStay = availableStays.find(
      (stay) => stay._id === formData.stay
    );
    const rate = selectedStay?.pricePerNight || 100;
    return nights * rate;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      const { stay, checkInDate, checkOutDate } = formData;
      if (!stay || !checkInDate || !checkOutDate)
        throw new Error("All fields are required");
      if (new Date(checkOutDate) <= new Date(checkInDate))
        throw new Error("Check-out must be after check-in");

      const totalAmount = calculateTotalAmount();
      const bookingData = {
        stay,
        checkInDate: new Date(checkInDate).toISOString(),
        checkOutDate: new Date(checkOutDate).toISOString(),
        totalAmount,
        status: "confirmed",
        nights: calculateNights(checkInDate, checkOutDate),
        user: currentUserId,
      };

      const response = await axios.post(
        "http://localhost:5003/api/bookings",
        bookingData
      );
      setBookings([...bookings, response.data]);
      setFormData({ stay: id || "", checkInDate: "", checkOutDate: "" });
      alert("Booking successful!");
    } catch (err) {
      console.error(err);
      setError(err.message || "Error submitting booking");
    } finally {
      setSubmitting(false);
    }
  };

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleCancel = async (bookingId) => {
    const confirmCancel = window.confirm(
      "Are you sure you want to cancel this booking?"
    );
    if (!confirmCancel) return;
    try {
      await axios.delete(`http://localhost:5003/api/bookings/${bookingId}`);
      setBookings(bookings.filter((booking) => booking._id !== bookingId));
      alert("Booking cancelled");
    } catch (err) {
      console.error("Cancel error:", err);
      alert("Error cancelling booking");
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold mb-4 text-gray-800">Book Your Stay</h2>

      {/* Booking Form */}
      <form
        onSubmit={handleSubmit}
        className="bg-white p-4 shadow rounded mb-8"
      >
        {error && <div className="text-red-600 mb-2">{error}</div>}
        <div className="mb-4">
          <label className="block font-semibold mb-1">Stay</label>
          <select
            name="stay"
            value={formData.stay}
            onChange={handleChange}
            className="border p-2 w-full"
            required
          >
            <option value="">Select a property</option>
            {availableStays.map((stay) => (
              <option key={stay._id} value={stay._id}>
                {stay.name} - ₹{stay.pricePerNight}/night
              </option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="block font-semibold mb-1">Check-In Date</label>
          <input
            type="date"
            name="checkInDate"
            value={formData.checkInDate}
            onChange={handleChange}
            className="border p-2 w-full"
            required
            min={new Date().toISOString().split("T")[0]}
          />
        </div>
        <div className="mb-4">
          <label className="block font-semibold mb-1">Check-Out Date</label>
          <input
            type="date"
            name="checkOutDate"
            value={formData.checkOutDate}
            onChange={handleChange}
            className="border p-2 w-full"
            required
            min={formData.checkInDate || new Date().toISOString().split("T")[0]}
          />
        </div>
        <div className="mb-4">
          <strong>Total: </strong> ₹{calculateTotalAmount()}
        </div>
        <button
          type="submit"
          disabled={submitting}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          {submitting ? "Booking..." : "Submit Booking"}
        </button>
      </form>

      {/* Booking List */}
      <h2 className="text-2xl font-bold mb-4 text-gray-800">All Bookings</h2>
      {loading ? (
        <p>Loading bookings...</p>
      ) : bookings.length === 0 ? (
        <p>No bookings found.</p>
      ) : (
        <ul className="space-y-4">
          {bookings.map((booking) => (
            <li
              key={booking._id}
              className="border border-gray-300 p-4 rounded-lg shadow-sm"
            >
              <p>
                <strong>User:</strong> {booking.user?.name || "N/A"}
              </p>
              <p>
                <strong>Stay:</strong> {booking.stay?.title || "N/A"}
              </p>
              <p>
                <strong>Check-In:</strong>{" "}
                {new Date(booking.checkInDate).toLocaleDateString()}
              </p>
              <p>
                <strong>Check-Out:</strong>{" "}
                {new Date(booking.checkOutDate).toLocaleDateString()}
              </p>
              <p>
                <strong>Total:</strong> ₹{booking.totalAmount}
              </p>
              <p>
                <strong>Status:</strong> {booking.status}
              </p>
              <button
                onClick={() => handleCancel(booking._id)}
                className="mt-2 bg-red-600 text-white px-3 py-1 rounded"
              >
                Cancel Booking
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default BookingManager;
