import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Booking = () => {
  const { id } = useParams();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [availableStays, setAvailableStays] = useState([]);
  const [staysLoading, setStaysLoading] = useState(true);
  const currentUserId = localStorage.getItem("userId");

  // Updated form data to match the MongoDB schema
  const [formData, setFormData] = useState({
    stay: id || "",
    checkInDate: "",
    checkOutDate: "",
  });

  // Initialize form with stay ID from URL params
  useEffect(() => {
    if (id) {
      setFormData((prev) => ({
        ...prev,
        stay: id,
      }));
    }
  }, [id]);

  // Fetch all bookings from backend
  useEffect(() => {
    fetchBookings();
  }, []);

  // Fetch all available stays separately - with proper error handling
  useEffect(() => {
    const fetchStays = async () => {
      setStaysLoading(true);
      try {
        console.log("Fetching available stays...");
        const response = await axios.get(
          "http://localhost:5003/api/stays/stay"
        );
        console.log("Available stays response:", response.data);

        if (response.data && Array.isArray(response.data)) {
          setAvailableStays(response.data);
        } else {
          console.error("Unexpected stay response format:", response.data);
          setError("Failed to load available properties");
        }
      } catch (error) {
        console.error("Error fetching available stays:", error);
        setError("Could not load available properties. Please try again.");
      } finally {
        setStaysLoading(false);
      }
    };

    fetchStays();
  }, []);

  const fetchBookings = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get("http://localhost:5003/api/bookings");
      console.log("Bookings API response:", response);

      if (response.data && Array.isArray(response.data)) {
        setBookings(response.data);
      } else {
        console.error("Unexpected bookings response format:", response.data);
        setBookings([]);
        setError("Invalid data format received from server");
      }
    } catch (error) {
      console.error("Error fetching bookings:", error);
      const errorMessage =
        error.response?.data?.message ||
        "Failed to load bookings. Please try again.";
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Calculate total amount based on check-in and check-out dates
  const calculateTotalAmount = () => {
    const checkIn = new Date(formData.checkInDate);
    const checkOut = new Date(formData.checkOutDate);

    // Calculate number of nights
    const nights = Math.max(
      Math.round((checkOut - checkIn) / (1000 * 60 * 60 * 24)),
      0
    );

    // Find the selected stay to get its price
    const selectedStay = availableStays.find(
      (stay) => stay._id === formData.stay
    );
    const nightlyRate = selectedStay?.pricePerNight || 100; // Default to 100 if not found

    return nights * nightlyRate;
  };

  // Return today's date in YYYY-MM-DD format for min date input
  const getTodayDate = () => {
    const today = new Date();
    return today.toISOString().split("T")[0];
  };

  // Format date for API submission (ISO format) to ensure consistency
  const formatDateForApi = (dateString) => {
    const date = new Date(dateString);
    return date.toISOString();
  };

  // Handle booking submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError(null);

    try {
      // Validate inputs
      if (!formData.stay.trim()) {
        throw new Error("Please select a property");
      }

      if (!formData.checkInDate || !formData.checkOutDate) {
        throw new Error("Check-in and check-out dates are required");
      }

      const checkIn = new Date(formData.checkInDate);
      const checkOut = new Date(formData.checkOutDate);

      if (checkOut <= checkIn) {
        throw new Error("Check-out date must be after check-in date");
      }

      // Calculate total amount
      const totalAmount = calculateTotalAmount();

      // Find the selected stay to include relevant information
      const selectedStay = availableStays.find(
        (stay) => stay._id === formData.stay
      );

      // Create booking data object with properly formatted dates
      // Include all fields that might be required by your backend
      const bookingData = {
        stay: formData.stay,
        checkInDate: formatDateForApi(formData.checkInDate),
        checkOutDate: formatDateForApi(formData.checkOutDate),
        totalAmount: parseFloat(totalAmount.toFixed(2)),
        status: "confirmed", // Add default status if required by backend
        // Include any other required fields for your API
        nights: calculateNights(formData.checkInDate, formData.checkOutDate),
        propertyName: selectedStay?.name || "Unknown Property",
        user: currentUserId,
      };

      console.log("Submitting booking data:", bookingData);

      const response = await axios.post(
        "http://localhost:5003/api/bookings",
        bookingData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      console.log("Booking submission response:", response);

      if (response.data) {
        setBookings([...bookings, response.data]);
        // Reset form but keep the stay ID if it came from URL params
        setFormData({
          stay: id || "",
          checkInDate: "",
          checkOutDate: "",
        });

        // Show success message
        const successMessage = document.getElementById("success-message");
        successMessage.classList.remove("hidden");
        setTimeout(() => {
          successMessage.classList.add("hidden");
        }, 3000);

        // Refresh the booking list
        fetchBookings();
      }
    } catch (error) {
      console.error("Error creating booking:", error);

      // Provide more specific error feedback
      let errorMessage = "Failed to create booking. Please try again.";

      if (error.message) {
        errorMessage = error.message;
      } else if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      } else if (error.response?.data?.error) {
        errorMessage = error.response.data.error;
      } else if (error.response?.status === 400) {
        errorMessage = "Invalid booking data. Please check your inputs.";

        // Log the specific validation errors if available
        if (error.response?.data) {
          console.error("Validation errors:", error.response.data);
        }
      } else if (error.response?.status === 404) {
        errorMessage = "Stay not found. Please check the Stay ID.";
      } else if (error.response?.status === 500) {
        errorMessage = "Server error. Please try again later.";
      }

      setError(errorMessage);
    } finally {
      setSubmitting(false);
    }
  };

  // Cancel booking
  const handleCancel = async (bookingId) => {
    const confirmCancel = window.confirm(
      "Are you sure you want to cancel this booking?"
    );
    if (!confirmCancel) return;

    try {
      await axios.delete(`http://localhost:5003/api/bookings/${bookingId}`);
      setBookings(bookings.filter((booking) => booking._id !== bookingId));

      // Show success message
      const successMessage = document.getElementById("cancel-success");
      successMessage.classList.remove("hidden");
      setTimeout(() => {
        successMessage.classList.add("hidden");
      }, 3000);
    } catch (error) {
      console.error("Error cancelling booking:", error);
      alert("Failed to cancel booking. Please try again.");
    }
  };

  // Format date for display
  const formatDate = (dateString) => {
    try {
      const options = {
        weekday: "long",
        year: "numeric",
        month: "long",
        day: "numeric",
      };
      return new Date(dateString).toLocaleDateString(undefined, options);
    } catch (e) {
      return dateString;
    }
  };

  // Calculate number of nights between two dates
  const calculateNights = (checkIn, checkOut) => {
    const start = new Date(checkIn);
    const end = new Date(checkOut);
    const nights = Math.round((end - start) / (1000 * 60 * 60 * 24));
    return nights > 0 ? nights : 0;
  };

  // Find the name of the selected stay
  const getSelectedStayName = () => {
    const selectedStay = availableStays.find(
      (stay) => stay._id === formData.stay
    );
    return selectedStay
      ? `${selectedStay.name} - $${selectedStay.pricePerNight}/night`
      : "Loading property details...";
  };

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Book Your Stay</h2>

      {/* Success Messages */}
      <div
        id="success-message"
        className="hidden mb-4 p-3 bg-green-100 text-green-700 border border-green-300 rounded"
      >
        Booking created successfully!
      </div>

      <div
        id="cancel-success"
        className="hidden mb-4 p-3 bg-green-100 text-green-700 border border-green-300 rounded"
      >
        Booking cancelled successfully!
      </div>

      {/* Error Message */}
      {error && (
        <div className="mb-4 p-3 bg-red-100 text-red-700 border border-red-300 rounded">
          {error}
        </div>
      )}

      {/* Booking Form */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8 border border-gray-200">
        <h3 className="text-xl font-semibold mb-4 text-gray-700">
          Reservation Details
        </h3>

        <form onSubmit={handleSubmit}>
          <div className="mt-4">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Select Property
            </label>

            {staysLoading ? (
              <div className="p-3 border border-gray-300 rounded-md bg-gray-50 flex items-center">
                <div className="mr-2 animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                <span>Loading available properties...</span>
              </div>
            ) : id ? (
              // If we have an ID from URL params, show it as selected but not editable
              <div className="p-3 bg-blue-50 border border-blue-200 rounded-md">
                <p className="font-medium">{getSelectedStayName()}</p>
              </div>
            ) : (
              // Normal dropdown selection
              <select
                name="stay"
                value={formData.stay}
                onChange={handleChange}
                required
                className="block w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Select a property</option>
                {availableStays.length > 0 ? (
                  availableStays.map((stay) => (
                    <option key={stay._id} value={stay._id}>
                      {stay.name} - ${stay.pricePerNight}/night
                    </option>
                  ))
                ) : (
                  <option value="" disabled>
                    No properties available
                  </option>
                )}
              </select>
            )}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Check-in Date
              </label>
              <input
                type="date"
                name="checkInDate"
                min={getTodayDate()}
                value={formData.checkInDate}
                onChange={handleChange}
                required
                className="block w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Check-out Date
              </label>
              <input
                type="date"
                name="checkOutDate"
                min={formData.checkInDate || getTodayDate()}
                value={formData.checkOutDate}
                onChange={handleChange}
                required
                className="block w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              />
              {formData.checkInDate && formData.checkOutDate && (
                <p className="text-xs text-gray-500 mt-1">
                  {calculateNights(formData.checkInDate, formData.checkOutDate)}{" "}
                  night(s)
                </p>
              )}
            </div>
          </div>

          {formData.stay &&
            formData.checkInDate &&
            formData.checkOutDate &&
            calculateNights(formData.checkInDate, formData.checkOutDate) >
              0 && (
              <div className="mt-4 p-3 bg-gray-50 border border-gray-200 rounded-md">
                <div className="flex justify-between items-center">
                  <span className="font-medium">Estimated Total:</span>
                  <span className="font-bold text-lg">
                    ${calculateTotalAmount()}
                  </span>
                </div>
                <p className="text-xs text-gray-500 mt-1">
                  This is an estimate. Final price may vary based on additional
                  fees and taxes.
                </p>
              </div>
            )}

          <div className="mt-6">
            <button
              type="submit"
              disabled={submitting}
              className={`w-full bg-blue-600 text-white py-3 px-6 rounded-md font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition ${
                submitting ? "opacity-70 cursor-not-allowed" : ""
              }`}
            >
              {submitting ? "Processing..." : "Complete Booking"}
            </button>
          </div>
        </form>
      </div>

      {/* Booking List */}
      <div className="bg-white shadow-md rounded-lg p-6 border border-gray-200">
        <h2 className="text-2xl font-bold mb-6 text-gray-800">Your Bookings</h2>

        {loading ? (
          <div className="flex items-center justify-center p-6">
            <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          <div>
            {bookings.length === 0 ? (
              <div className="text-center p-6 text-gray-500">
                <p>No bookings found.</p>
                <p className="text-sm mt-2">
                  Once you make a booking, it will appear here.
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {bookings.map((booking) => (
                  <div
                    key={booking._id}
                    className="p-4 border rounded-lg shadow-sm hover:shadow-md transition-shadow bg-gray-50"
                  >
                    <div className="flex flex-col md:flex-row justify-between">
                      <div className="mb-4 md:mb-0">
                        <div className="flex items-center mb-2">
                          <span
                            className={`inline-block px-2 py-1 text-xs rounded ${
                              booking.status === "confirmed"
                                ? "bg-green-100 text-green-800"
                                : booking.status === "cancelled"
                                ? "bg-red-100 text-red-800"
                                : "bg-yellow-100 text-yellow-800"
                            }`}
                          >
                            {booking.status?.charAt(0).toUpperCase() +
                              booking.status?.slice(1) || "Pending"}
                          </span>
                        </div>

                        <h3 className="font-bold text-lg text-gray-800">
                          {booking.stay?.title || "Booking"} #
                          {booking._id?.slice(-6)}
                        </h3>

                        <div className="mt-2 space-y-1">
                          <p className="text-gray-700">
                            <span className="font-medium">User:</span>{" "}
                            {booking.user?.name || "N/A"}
                          </p>
                          <p className="text-gray-700">
                            <span className="font-medium">Check-in:</span>{" "}
                            {formatDate(booking.checkInDate)}
                          </p>
                          <p className="text-gray-700">
                            <span className="font-medium">Check-out:</span>{" "}
                            {formatDate(booking.checkOutDate)}
                          </p>
                          <p className="text-gray-700">
                            <span className="font-medium">Nights:</span>{" "}
                            {calculateNights(
                              booking.checkInDate,
                              booking.checkOutDate
                            )}
                          </p>
                          <p className="text-gray-700">
                            <span className="font-medium">Total:</span> $
                            {booking.totalAmount?.toFixed(2)}
                          </p>
                          <p className="text-gray-700">
                            <span className="font-medium">Stay ID:</span>{" "}
                            <span className="font-mono text-sm">
                              {booking.stay}
                            </span>
                          </p>
                        </div>
                      </div>
                      <div className="flex items-start">
                        {booking.status !== "cancelled" && (
                          <button
                            onClick={() => handleCancel(booking._id)}
                            className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md transition"
                          >
                            Cancel Booking
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Booking;
