import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Food = () => {
  const [foodItems, setFoodItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFood = async () => {
      try {
        const res = await axios.get("http://localhost:5003/api/foods");
        console.log("API Response for Food:", res.data);

        if (res.data && Array.isArray(res.data)) {
          setFoodItems(res.data);
        } else {
          console.error("Unexpected response format:", res.data);
          setFoodItems([]);
        }
      } catch (err) {
        console.error("Error fetching food:", err);
        setError("Failed to load food items. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchFood();
  }, []);

  // Function to handle order button click
  const handleOrderFood = () => {
    navigate("/booking"); // Redirect to booking page
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center">Delicious Food</h1>

      {/* Loading State */}
      {loading && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {[...Array(3)].map((_, i) => (
            <div
              key={i}
              className="bg-gray-100 p-4 rounded-lg animate-pulse h-40"
            >
              <div className="h-5 bg-gray-200 rounded w-3/4 mb-4"></div>
              <div className="h-3 bg-gray-200 rounded w-full mb-2"></div>
              <div className="h-3 bg-gray-200 rounded w-5/6"></div>
            </div>
          ))}
        </div>
      )}

      {/* Error State */}
      {error && (
        <div className="bg-red-100 text-red-700 p-4 rounded-lg mb-4">
          {error}
        </div>
      )}

      {/* No Data Available */}
      {!loading && foodItems.length === 0 && !error && (
        <div className="text-center p-8 bg-gray-50 rounded-lg">
          No food items found.
        </div>
      )}

      {/* Data Display */}
      {!loading && foodItems.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {foodItems.map((item) => (
            <div
              key={item._id || item.id}
              className="bg-white shadow-md rounded-lg overflow-hidden border border-gray-200"
            >
              {item.image && (
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-full h-48 object-cover"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "/api/placeholder/400/300";
                  }}
                />
              )}
              <div className="p-4">
                <h3 className="text-xl font-semibold mb-2">{item.name}</h3>
                <p className="text-gray-600 mb-3">
                  {item.description
                    ? item.description
                    : "No description available"}
                </p>
                <div className="flex justify-between items-center">
                  <span className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded">
                    {item.category || "General"}
                  </span>
                  <span className="font-bold text-lg">
                    ${item.price ? item.price.toFixed(2) : "N/A"}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Order Food Button */}
      <div className="mt-8 text-center">
        <button
          onClick={handleOrderFood}
          className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-6 rounded-lg"
        >
          Order Food
        </button>
      </div>
    </div>
  );
};

export default Food;
