import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Booking = () => {
  const { id } = useParams();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [availableStays, setAvailableStays] = useState([]);
  const [staysLoading, setStaysLoading] = useState(true);
  const currentUserId = localStorage.getItem("userId");

  const [formData, setFormData] = useState({
    stay: id || "",
    checkInDate: "",
    checkOutDate: "",
  });

  useEffect(() => {
    if (id) {
      setFormData((prev) => ({
        ...prev,
        stay: id,
      }));
    }
  }, [id]);

  useEffect(() => {
    fetchBookings();
  }, []);

  useEffect(() => {
    const fetchStays = async () => {
      setStaysLoading(true);
      try {
        const response = await axios.get(
          "http://localhost:5003/api/stays/stay"
        );
        if (response.data && Array.isArray(response.data)) {
          setAvailableStays(response.data);
        } else {
          setError("Failed to load available properties");
        }
      } catch (error) {
        console.error("Error fetching stays:", error);
        setError("Could not load available properties.");
      } finally {
        setStaysLoading(false);
      }
    };
    fetchStays();
  }, []);

  const fetchBookings = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get("http://localhost:5003/api/bookings");
      if (response.data && Array.isArray(response.data)) {
        setBookings(response.data);
      } else {
        setError("Invalid data format received from server");
      }
    } catch (error) {
      console.error("Error fetching bookings:", error);
      setError("Failed to load bookings.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const calculateTotalAmount = () => {
    const checkIn = new Date(formData.checkInDate);
    const checkOut = new Date(formData.checkOutDate);
    const nights = Math.max(
      Math.round((checkOut - checkIn) / (1000 * 60 * 60 * 24)),
      0
    );
    const selectedStay = availableStays.find(
      (stay) => stay._id === formData.stay
    );
    const nightlyRate = selectedStay?.pricePerNight || 100;
    return nights * nightlyRate;
  };

  const formatDateForApi = (dateString) => {
    const date = new Date(dateString);
    return date.toISOString();
  };

  const calculateNights = (checkIn, checkOut) => {
    const start = new Date(checkIn);
    const end = new Date(checkOut);
    const nights = Math.round((end - start) / (1000 * 60 * 60 * 24));
    return nights > 0 ? nights : 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError(null);

    try {
      if (!formData.stay.trim()) throw new Error("Please select a property");
      if (!formData.checkInDate || !formData.checkOutDate)
        throw new Error("Check-in and check-out dates are required");

      const checkIn = new Date(formData.checkInDate);
      const checkOut = new Date(formData.checkOutDate);
      if (checkOut <= checkIn)
        throw new Error("Check-out date must be after check-in date");

      const totalAmount = calculateTotalAmount();
      const selectedStay = availableStays.find(
        (stay) => stay._id === formData.stay
      );

      const bookingData = {
        stay: formData.stay,
        checkInDate: formatDateForApi(formData.checkInDate),
        checkOutDate: formatDateForApi(formData.checkOutDate),
        totalAmount: parseFloat(totalAmount.toFixed(2)),
        status: "confirmed",
        nights: calculateNights(formData.checkInDate, formData.checkOutDate),
        propertyName: selectedStay?.name || "Unknown Property",
        user: currentUserId,
      };

      const response = await axios.post(
        "http://localhost:5003/api/bookings",
        bookingData,
        {
          headers: { "Content-Type": "application/json" },
        }
      );

      if (response.data) {
        setBookings([...bookings, response.data]);
        setFormData({ stay: id || "", checkInDate: "", checkOutDate: "" });
        fetchBookings();
      }
    } catch (error) {
      console.error("Error creating booking:", error);
      setError(error.message || "Booking failed. Try again.");
    } finally {
      setSubmitting(false);
    }
  };

  const handleCancel = async (bookingId) => {
    const confirmCancel = window.confirm(
      "Are you sure you want to cancel this booking?"
    );
    if (!confirmCancel) return;

    try {
      await axios.delete(`http://localhost:5003/api/bookings/${bookingId}`);
      setBookings(bookings.filter((booking) => booking._id !== bookingId));
    } catch (error) {
      console.error("Error cancelling booking:", error);
      alert("Failed to cancel booking.");
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Book Your Stay</h2>

      {error && (
        <div className="mb-4 p-3 bg-red-100 text-red-700 border border-red-300 rounded">
          {error}
        </div>
      )}

      {/* Booking Form */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8 border border-gray-200">
        <h3 className="text-xl font-semibold mb-4 text-gray-700">
          Reservation Details
        </h3>
        <form onSubmit={handleSubmit}>
          <div className="mt-4">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Select Property
            </label>
            {staysLoading ? (
              <p>Loading stays...</p>
            ) : (
              <select
                name="stay"
                value={formData.stay}
                onChange={handleChange}
                className="border p-2 rounded w-full"
              >
                <option value="">Select a stay</option>
                {availableStays.map((stay) => (
                  <option key={stay._id} value={stay._id}>
                    {stay.name} - ₹{stay.pricePerNight}/night
                  </option>
                ))}
              </select>
            )}
          </div>

          <div className="mt-4">
            <label className="block mb-1">Check-In Date</label>
            <input
              type="date"
              name="checkInDate"
              value={formData.checkInDate}
              onChange={handleChange}
              className="border p-2 rounded w-full"
            />
          </div>

          <div className="mt-4">
            <label className="block mb-1">Check-Out Date</label>
            <input
              type="date"
              name="checkOutDate"
              value={formData.checkOutDate}
              onChange={handleChange}
              className="border p-2 rounded w-full"
            />
          </div>

          <div className="mt-4">
            <strong>Total Amount:</strong> ₹{calculateTotalAmount()}
          </div>

          <button
            type="submit"
            disabled={submitting}
            className="mt-6 bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
          >
            {submitting ? "Booking..." : "Confirm Booking"}
          </button>
        </form>
      </div>

      {/* All Bookings Section */}
      <div style={{ padding: "1rem" }}>
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">
          All Bookings
        </h2>
        <ul style={{ listStyle: "none", padding: 0 }}>
          {bookings.map((booking) => (
            <li
              key={booking._id}
              style={{
                marginBottom: "1rem",
                border: "1px solid #ccc",
                padding: "1rem",
                borderRadius: "8px",
              }}
            >
              <div>
                <strong>User:</strong> {booking.user?.name || "N/A"} <br />
                <strong>Stay:</strong> {booking.stay?.title || "N/A"} <br />
                <strong>Check-In:</strong>{" "}
                {new Date(booking.checkInDate).toLocaleDateString()} <br />
                <strong>Check-Out:</strong>{" "}
                {new Date(booking.checkOutDate).toLocaleDateString()} <br />
                <strong>Total:</strong> ₹{booking.totalAmount} <br />
                <strong>Status:</strong> {booking.status}
              </div>
              <button
                onClick={() => handleCancel(booking._id)}
                style={{
                  marginTop: "0.5rem",
                  backgroundColor: "#f44336",
                  color: "white",
                  border: "none",
                  padding: "0.5rem 1rem",
                  borderRadius: "4px",
                  cursor: "pointer",
                }}
              >
                Cancel Booking
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Booking;
