import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CheckoutForm from "../components/CheckoutForm";
import OrderSummary from "../components/OrderSummary";
import PaymentForm from "../components/PaymentForm";
import Receipt from "../components/Receipt";
import "./Order.css";

const Order = () => {
  const [cart, setCart] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);
  const [currentStep, setCurrentStep] = useState("checkout"); // checkout, summary, payment, receipt
  const [order, setOrder] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState(null);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  // Load cart from localStorage
  useEffect(() => {
    const savedCart = localStorage.getItem("foodCart");
    const savedTotal = localStorage.getItem("cartTotal");

    if (savedCart) {
      setCart(JSON.parse(savedCart));
    } else {
      // Redirect to menu if no cart
      navigate("/");
    }

    if (savedTotal) {
      setCartTotal(parseFloat(savedTotal));
    }
  }, [navigate]);

  // Handle checkout form submission
  const handleCheckoutSubmit = async (formData) => {
    try {
      const orderData = {
        customer: formData.customerName,
        address: formData.address,
        contactNumber: formData.contactNumber,
        items: cart.map((item) => ({
          foodId: item.id,
          quantity: item.quantity,
        })),
      };

      const response = await fetch("/api/place-order", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(orderData),
      });

      if (!response.ok) {
        throw new Error("Failed to place order");
      }

      const data = await response.json();

      // Fetch order details
      const orderResponse = await fetch(`/api/order/${data.orderId}`);

      if (!orderResponse.ok) {
        throw new Error("Failed to fetch order details");
      }

      const orderDetails = await orderResponse.json();
      setOrder(orderDetails);
      setCurrentStep("summary");
    } catch (err) {
      setError(err.message);
      console.error("Error during checkout:", err);
    }
  };

  // Proceed to payment
  const proceedToPayment = () => {
    setCurrentStep("payment");
  };

  // Handle payment form submission
  const handlePaymentSubmit = async (paymentData) => {
    try {
      const response = await fetch("/api/payment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(paymentData),
      });

      if (!response.ok) {
        throw new Error("Failed to process payment");
      }

      const data = await response.json();

      // Update order in state
      const updatedOrderResponse = await fetch(`/api/order/${order._id}`);
      if (updatedOrderResponse.ok) {
        const updatedOrder = await updatedOrderResponse.json();
        setOrder(updatedOrder);
      }

      setPaymentMethod(paymentData.paymentMethod);
      setCurrentStep("receipt");

      // Store receipt URL
      localStorage.setItem("receiptUrl", data.receiptUrl);
    } catch (err) {
      setError(err.message);
      console.error("Error during payment:", err);
    }
  };

  // Download receipt
  const downloadReceipt = () => {
    const receiptUrl = localStorage.getItem("receiptUrl");
    if (receiptUrl) {
      window.location.href = receiptUrl;
    } else {
      setError("Receipt URL not found");
    }
  };

  // Place new order
  const placeNewOrder = () => {
    // Clear cart and related data
    localStorage.removeItem("foodCart");
    localStorage.removeItem("cartTotal");
    localStorage.removeItem("receiptUrl");

    // Navigate back to menu
    navigate("/");
  };

  if (error) {
    return (
      <div className="order-error">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => navigate("/")}>Back to Menu</button>
      </div>
    );
  }

  return (
    <div className="order-page">
      <header className="order-header">
        <h1>Complete Your Order</h1>
      </header>

      <div className="order-progress">
        <div
          className={`progress-step ${
            currentStep === "checkout" ? "active" : ""
          } ${currentStep !== "checkout" ? "completed" : ""}`}
        >
          Checkout
        </div>
        <div
          className={`progress-step ${
            currentStep === "summary" ? "active" : ""
          } ${
            currentStep !== "checkout" && currentStep !== "summary"
              ? "completed"
              : ""
          }`}
        >
          Order Summary
        </div>
        <div
          className={`progress-step ${
            currentStep === "payment" ? "active" : ""
          } ${currentStep === "receipt" ? "completed" : ""}`}
        >
          Payment
        </div>
        <div
          className={`progress-step ${
            currentStep === "receipt" ? "active" : ""
          }`}
        >
          Receipt
        </div>
      </div>

      <div className="order-content">
        {currentStep === "checkout" && (
          <CheckoutForm onSubmit={handleCheckoutSubmit} />
        )}

        {currentStep === "summary" && order && (
          <OrderSummary order={order} proceedToPayment={proceedToPayment} />
        )}

        {currentStep === "payment" && order && (
          <PaymentForm
            orderId={order._id}
            totalAmount={order.totalAmount}
            onSubmit={handlePaymentSubmit}
          />
        )}

        {currentStep === "receipt" && order && (
          <Receipt
            order={order}
            paymentMethod={paymentMethod}
            downloadReceipt={downloadReceipt}
            placeNewOrder={placeNewOrder}
          />
        )}
      </div>

      <div className="order-actions">
        {currentStep !== "checkout" && currentStep !== "receipt" && (
          <button
            className="back-btn"
            onClick={() => {
              if (currentStep === "summary") setCurrentStep("checkout");
              if (currentStep === "payment") setCurrentStep("summary");
            }}
          >
            Back
          </button>
        )}
      </div>
    </div>
  );
};

export default Order;
