import React from "react";
import PropTypes from "prop-types";
import "./Receipt.css";

const Receipt = ({ order, paymentMethod, downloadReceipt, placeNewOrder }) => {
  return (
    <div className="receipt-container">
      <h2>Payment Complete</h2>
      <div className="success-message">
        <p>Your payment has been processed successfully!</p>
        <p>Your order is being prepared.</p>
      </div>
      <div className="receipt-details">
        <h3>Receipt Details</h3>
        <p>
          <strong>Order ID:</strong> {order._id}
        </p>
        <p>
          <strong>Date:</strong> {new Date(order.orderDate).toLocaleString()}
        </p>
        <p>
          <strong>Payment Method:</strong> {paymentMethod}
        </p>
        <p>
          <strong>Total Amount:</strong> ${order.totalAmount.toFixed(2)}
        </p>
      </div>
      <div className="receipt-actions">
        <button className="download-btn" onClick={downloadReceipt}>
          Download Receipt
        </button>
        <button className="new-order-btn" onClick={placeNewOrder}>
          Place New Order
        </button>
      </div>
    </div>
  );
};

Receipt.propTypes = {
  order: PropTypes.object.isRequired,
  paymentMethod: PropTypes.string.isRequired,
  downloadReceipt: PropTypes.func.isRequired,
  placeNewOrder: PropTypes.func.isRequired,
};

export default Receipt;
