import { motion } from "framer-motion";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const Home = () => {
  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />

      {/* Hero Section */}
      <section className="relative flex flex-col items-center justify-center text-center h-[80vh] bg-cover bg-center bg-[url('/images/hero-bg.jpg')]">
        <div className="absolute inset-0 bg-black/50"></div>
        <motion.div
          className="relative z-10 text-white"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="text-5xl md:text-7xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-blue-400 to-teal-400">
            Welcome to Stayzo
          </h1>
          <p className="mt-4 text-lg md:text-xl max-w-2xl">
            Find the best places to stay, dine, and relax with top-tier
            services.
          </p>
          <motion.button
            className="mt-6 px-6 py-3 bg-teal-500 text-white font-semibold rounded-full shadow-lg hover:bg-teal-600 transition-all duration-300"
            whileHover={{ scale: 1.1 }}
          >
            Explore Now
          </motion.button>
        </motion.div>
      </section>

      {/* Featured Stays */}
      <section className="py-16 px-6 md:px-20">
        <h2 className="text-4xl font-bold text-gray-900 text-center">
          Featured Stays
        </h2>
        <p className="text-gray-600 text-center mt-2">
          Top-rated places curated just for you
        </p>

        <div className="mt-10 grid grid-cols-1 md:grid-cols-3 gap-8">
          {["Luxury Hotel", "Beachside Villa", "Mountain Retreat"].map(
            (stay, index) => (
              <motion.div
                key={index}
                className="bg-white rounded-xl shadow-xl overflow-hidden transform hover:scale-105 transition-all duration-300"
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.2 }}
              >
                <img
                  src={`/images/stay-${index + 1}.jpg`}
                  alt={stay}
                  className="w-full h-56 object-cover"
                />
                <div className="p-6">
                  <h3 className="text-2xl font-semibold">{stay}</h3>
                  <p className="text-gray-600 mt-2">
                    Experience world-class comfort in the best locations.
                  </p>
                  <button className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition">
                    Book Now
                  </button>
                </div>
              </motion.div>
            )
          )}
        </div>
      </section>

      {/* CTA */}
      <section className="bg-gradient-to-r from-blue-600 to-teal-500 py-16 text-white text-center">
        <h2 className="text-4xl font-bold">Ready for Your Next Adventure?</h2>
        <p className="mt-3 text-lg">
          Book your dream stay today and make unforgettable memories.
        </p>
        <motion.button
          className="mt-5 px-6 py-3 bg-white text-blue-600 font-semibold rounded-full shadow-md hover:bg-gray-200 transition-all duration-300"
          whileHover={{ scale: 1.1 }}
        >
          Get Started
        </motion.button>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
