import { useState, useEffect } from "react";
import axios from "axios";
import FoodCard from "../components/FoodCard"; // ✅ Import FoodCard

const Food = ({ addToCart }) => {
  const [foods, setFoods] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5003/foods")
      .then((res) => setFoods(res.data))
      .catch((err) => console.error("Error fetching food:", err));
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Popular Food</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {foods.length > 0 ? (
          foods.map((food) => (
            <FoodCard key={food._id} food={food} addToCart={addToCart} />
          ))
        ) : (
          <p className="text-gray-600">No food items available.</p> // ✅ Added fallback UI
        )}
      </div>
    </div>
  );
};

export default Food;
