import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const Payment = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { bookingDetails, amount } = location.state || { amount: 0 };

  const [paymentData, setPaymentData] = useState({
    cardNumber: "",
    cardHolder: "",
    cvv: "",
    expiryDate: "",
  });

  const handleChange = (e) => {
    setPaymentData({ ...paymentData, [e.target.name]: e.target.value });
  };

  // Generate CSV content
  const generateCSV = () => {
    const today = new Date().toLocaleDateString();
    const invoiceNumber = "INV-" + Math.floor(100000 + Math.random() * 900000);

    // Format check-in and check-out dates
    const checkIn = bookingDetails?.checkInDate
      ? new Date(bookingDetails.checkInDate).toLocaleDateString()
      : "N/A";
    const checkOut = bookingDetails?.checkOutDate
      ? new Date(bookingDetails.checkOutDate).toLocaleDateString()
      : "N/A";

    // User and Stay information
    const userName = bookingDetails?.user?.name || "Guest";
    const stayName = bookingDetails?.stay?.title || "Accommodation";

    // Create CSV content
    const csvContent = [
      "BOOKING AND PAYMENT INVOICE",
      `Invoice Number,${invoiceNumber}`,
      `Invoice Date,${today}`,
      "",
      "CUSTOMER DETAILS",
      `Customer Name,${userName}`,
      `User ID,${bookingDetails?.user || "N/A"}`,
      "",
      "BOOKING DETAILS",
      `Accommodation,${stayName}`,
      `Stay ID,${bookingDetails?.stay || "N/A"}`,
      `Check-in Date,${checkIn}`,
      `Check-out Date,${checkOut}`,
      "",
      "PAYMENT DETAILS",
      `Payment Method,Credit Card (ending in ${
        paymentData.cardNumber.slice(-4) || "XXXX"
      })`,
      `Payment Date,${today}`,
      `Amount Paid,₹${amount || bookingDetails?.totalAmount || 0}`,
      `Payment Status,Completed`,
      "",
      "Thank you for your booking!",
    ].join("\n");

    return csvContent;
  };

  // Download CSV file
  const downloadCSV = (csvContent) => {
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");

    // Format filename with date
    const date = new Date().toISOString().slice(0, 10);
    link.setAttribute("href", url);
    link.setAttribute("download", `booking-invoice-${date}.csv`);
    document.body.appendChild(link);

    link.click();
    document.body.removeChild(link);
  };

  const handlePayment = () => {
    // Basic validation
    if (
      !paymentData.cardNumber ||
      !paymentData.cardHolder ||
      !paymentData.cvv
    ) {
      alert("Please fill all payment fields");
      return;
    }

    // Generate and download CSV
    const csvContent = generateCSV();
    downloadCSV(csvContent);

    alert("Payment Successful! Invoice has been downloaded.");
    navigate("/");
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Payment</h2>

      {bookingDetails && (
        <div className="mb-4 p-4 bg-gray-100 rounded">
          <h3 className="font-bold">Booking Summary</h3>
          {bookingDetails.stay?.title && (
            <p>Stay: {bookingDetails.stay.title}</p>
          )}
          {bookingDetails.checkInDate && (
            <p>
              Check-in:{" "}
              {new Date(bookingDetails.checkInDate).toLocaleDateString()}
            </p>
          )}
          {bookingDetails.checkOutDate && (
            <p>
              Check-out:{" "}
              {new Date(bookingDetails.checkOutDate).toLocaleDateString()}
            </p>
          )}
          <p className="font-bold mt-2">
            Total Amount: ₹{amount || bookingDetails.totalAmount}
          </p>
        </div>
      )}

      <input
        type="text"
        name="cardNumber"
        placeholder="Card Number"
        className="block w-full p-2 mb-2 border rounded"
        value={paymentData.cardNumber}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="cardHolder"
        placeholder="Card Holder Name"
        className="block w-full p-2 mb-2 border rounded"
        value={paymentData.cardHolder}
        onChange={handleChange}
        required
      />
      <div className="flex gap-2 mb-2">
        <input
          type="text"
          name="cvv"
          placeholder="CVV"
          className="block w-1/3 p-2 border rounded"
          value={paymentData.cvv}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="expiryDate"
          placeholder="MM/YY"
          className="block w-2/3 p-2 border rounded"
          value={paymentData.expiryDate}
          onChange={handleChange}
        />
      </div>
      <button
        onClick={handlePayment}
        className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition"
      >
        Confirm Payment & Download Invoice
      </button>
    </div>
  );
};

export default Payment;
