import React, { useState, useEffect } from "react";
import axios from "axios";
import "./booking.css"; // Make sure to create this CSS file with the styles below

const Booking = () => {
  const [formData, setFormData] = useState({
    user: "",
    stay: "",
    checkInDate: "",
    checkOutDate: "",
    totalAmount: "",
  });

  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [formVisible, setFormVisible] = useState(true);
  const [activeTab, setActiveTab] = useState("create");
  const [notification, setNotification] = useState({
    show: Tr,
    message: "",
    type: "",
  });
  const [filterStatus, setFilterStatus] = useState("all");

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const calculateNights = () => {
    if (!formData.checkInDate || !formData.checkOutDate) return 0;
    const checkIn = new Date(formData.checkInDate);
    const checkOut = new Date(formData.checkOutDate);
    return Math.max(
      Math.round((checkOut - checkIn) / (1000 * 60 * 60 * 24)),
      0
    );
  };

  const showNotification = (message, type) => {
    setNotification({ show: true, message, type });
    setTimeout(() => {
      setNotification({ show: false, message: "", type: "" });
    }, 3000);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      // Validate dates
      const checkIn = new Date(formData.checkInDate);
      const checkOut = new Date(formData.checkOutDate);

      if (checkOut <= checkIn) {
        throw new Error("Check-out date must be after check-in date");
      }

      const response = await axios.post("http://localhost:5003/api/bookings", {
        ...formData,
        status: "confirmed",
      });

      showNotification("Booking created successfully!", "success");

      setFormData({
        user: "",
        stay: "",
        checkInDate: "",
        checkOutDate: "",
        totalAmount: "",
      });

      fetchBookings();
    } catch (err) {
      console.error(err);
      showNotification(err.message || "Failed to create booking", "error");
    } finally {
      setSubmitting(false);
    }
  };

  const fetchBookings = async () => {
    setLoading(true);
    try {
      const res = await axios.get("http://localhost:5003/api/bookings");
      setBookings(res.data);
    } catch (err) {
      console.error("Error fetching bookings:", err);
      showNotification("Error loading bookings", "error");
    } finally {
      setLoading(false);
    }
  };

  const cancelBooking = async (id) => {
    try {
      await axios.delete(`http://localhost:5003/api/bookings/${id}`);
      showNotification("Booking cancelled successfully", "success");
      fetchBookings();
    } catch (err) {
      showNotification("Error cancelling booking", "error");
    }
  };

  const formatDate = (dateString) => {
    try {
      const options = {
        weekday: "short",
        year: "numeric",
        month: "short",
        day: "numeric",
      };
      return new Date(dateString).toLocaleDateString(undefined, options);
    } catch (e) {
      return dateString;
    }
  };

  const filteredBookings =
    filterStatus === "all"
      ? bookings
      : bookings.filter((booking) => booking.status === filterStatus);

  useEffect(() => {
    fetchBookings();

    // Add scroll event listener for parallax effect
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const headerBg = document.querySelector(".booking-header-bg");
      if (headerBg) {
        headerBg.style.transform = `translateY(${scrollY * 0.5}px)`;
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="booking-container">
      {/* Notification */}
      {notification.show && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}

      {/* Header with parallax effect */}
      <div className="booking-header">
        <div className="booking-header-bg"></div>
        <div className="booking-header-content">
          <h1>Luxury Stays Booking</h1>
          <p>Book your dream vacation with just a few clicks</p>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="booking-tabs">
        <button
          className={`tab-button ${activeTab === "create" ? "active" : ""}`}
          onClick={() => setActiveTab("create")}
        >
          Create Booking
        </button>
        <button
          className={`tab-button ${activeTab === "list" ? "active" : ""}`}
          onClick={() => setActiveTab("list")}
        >
          View Bookings
        </button>
      </div>

      {/* Create Booking Form */}
      {activeTab === "create" && (
        <div
          className={`booking-form-container ${formVisible ? "visible" : ""}`}
        >
          <div className="form-header">
            <h2>Create New Booking</h2>
            <button
              className="toggle-form-btn"
              onClick={() => setFormVisible(!formVisible)}
            >
              {formVisible ? "−" : "+"}
            </button>
          </div>

          {formVisible && (
            <form onSubmit={handleSubmit} className="booking-form">
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="user">Guest Information</label>
                  <input
                    id="user"
                    name="user"
                    placeholder="User ID"
                    onChange={handleChange}
                    value={formData.user}
                    required
                    className="input-field"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="stay">Property Selection</label>
                  <input
                    id="stay"
                    name="stay"
                    placeholder="Stay ID"
                    onChange={handleChange}
                    value={formData.stay}
                    required
                    className="input-field"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="checkInDate">Check-In Date</label>
                  <div className="date-input-container">
                    <input
                      id="checkInDate"
                      type="date"
                      name="checkInDate"
                      onChange={handleChange}
                      value={formData.checkInDate}
                      required
                      className="input-field date-input"
                    />
                    <i className="calendar-icon"></i>
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="checkOutDate">Check-Out Date</label>
                  <div className="date-input-container">
                    <input
                      id="checkOutDate"
                      type="date"
                      name="checkOutDate"
                      onChange={handleChange}
                      value={formData.checkOutDate}
                      required
                      className="input-field date-input"
                    />
                    <i className="calendar-icon"></i>
                  </div>
                </div>
              </div>

              {formData.checkInDate && formData.checkOutDate && (
                <div className="nights-display">
                  {calculateNights()} night{calculateNights() !== 1 ? "s" : ""}
                </div>
              )}

              <div className="form-group">
                <label htmlFor="totalAmount">Total Amount</label>
                <div className="amount-input-container">
                  <span className="currency-symbol">₹</span>
                  <input
                    id="totalAmount"
                    name="totalAmount"
                    type="number"
                    placeholder="Total Amount"
                    onChange={handleChange}
                    value={formData.totalAmount}
                    required
                    className="input-field amount-input"
                  />
                </div>
              </div>

              <button
                type="submit"
                className={`submit-button ${submitting ? "loading" : ""}`}
                disabled={submitting}
              >
                {submitting ? (
                  <span className="loading-spinner"></span>
                ) : (
                  "Complete Booking"
                )}
              </button>
            </form>
          )}
        </div>
      )}

      {/* Bookings List */}
      {activeTab === "list" && (
        <div className="bookings-list-container">
          <div className="list-header">
            <h2>Your Bookings</h2>
            <div className="filter-controls">
              <label htmlFor="status-filter">Filter by:</label>
              <select
                id="status-filter"
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
                className="status-filter"
              >
                <option value="all">All Bookings</option>
                <option value="confirmed">Confirmed</option>
                <option value="cancelled">Cancelled</option>
                <option value="pending">Pending</option>
              </select>
            </div>
          </div>

          {loading ? (
            <div className="loading-container">
              <div className="loading-spinner large"></div>
              <p>Loading your bookings...</p>
            </div>
          ) : filteredBookings.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon"></div>
              <h3>No bookings found</h3>
              <p>Once you make a booking, it will appear here.</p>
              <button
                className="create-booking-btn"
                onClick={() => setActiveTab("create")}
              >
                Create a New Booking
              </button>
            </div>
          ) : (
            <div className="bookings-grid">
              {filteredBookings.map((booking) => (
                <div
                  key={booking._id}
                  className={`booking-card ${booking.status}`}
                  data-aos="fade-up"
                >
                  <div className="booking-card-header">
                    <div className="booking-status-badge">{booking.status}</div>
                    <div className="booking-id">#{booking._id?.slice(-6)}</div>
                  </div>

                  <div className="booking-card-content">
                    <div className="booking-property">
                      {booking.stay?.title || "Unnamed Property"}
                    </div>

                    <div className="booking-guest">
                      <div className="guest-avatar"></div>
                      <div className="guest-name">
                        {booking.user?.name || "Guest"}
                      </div>
                    </div>

                    <div className="booking-dates">
                      <div className="date-group">
                        <div className="date-label">Check-in</div>
                        <div className="date-value">
                          {formatDate(booking.checkInDate)}
                        </div>
                      </div>

                      <div className="dates-separator">
                        <div className="dates-line"></div>
                        <div className="dates-nights">
                          {calculateNights(
                            booking.checkInDate,
                            booking.checkOutDate
                          )}{" "}
                          nights
                        </div>
                        <div className="dates-line"></div>
                      </div>

                      <div className="date-group">
                        <div className="date-label">Check-out</div>
                        <div className="date-value">
                          {formatDate(booking.checkOutDate)}
                        </div>
                      </div>
                    </div>

                    <div className="booking-price">
                      <div className="price-label">Total</div>
                      <div className="price-value">₹{booking.totalAmount}</div>
                    </div>
                  </div>

                  <div className="booking-card-actions">
                    {booking.status !== "cancelled" && (
                      <button
                        onClick={() => cancelBooking(booking._id)}
                        className="cancel-button"
                      >
                        Cancel Booking
                      </button>
                    )}
                    <button className="details-button">View Details</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Booking;
