import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { motion } from "framer-motion";

const Home = () => {
  return (
    <div className="relative bg-gray-900 text-white">
      {/* Hero Section with Parallax Effect */}
      <div
        className="relative h-screen flex flex-col items-center justify-center bg-cover bg-center"
        style={{
          backgroundImage: "url('/images/hero.jpg')",
        }}
      >
        <motion.h1
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          className="text-5xl md:text-6xl font-extrabold drop-shadow-lg"
        >
          Welcome to Stayzo
        </motion.h1>
        <motion.p
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.2 }}
          className="mt-4 text-lg md:text-xl text-gray-300"
        >
          Discover the best places to stay and eat, curated for you.
        </motion.p>
        <motion.a
          href="/listings"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          className="mt-6 px-6 py-3 bg-blue-500 hover:bg-blue-600 text-white font-semibold rounded-full shadow-lg transition"
        >
          Explore Now
        </motion.a>
      </div>

      {/* Featured Listings Section */}
      <div className="py-20 px-6 md:px-16 bg-gray-900">
        <h2 className="text-4xl font-bold text-center text-white mb-10">
          Featured Stays & Restaurants
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
          {/* Featured Cards */}
          {[
            { name: "Luxury Villa", img: "/images/villa.jpg" },
            { name: "Seaside Resort", img: "/images/resort.jpg" },
            { name: "Fine Dining", img: "/images/restaurant.jpg" },
          ].map((item, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.05 }}
              className="relative overflow-hidden rounded-2xl shadow-lg"
            >
              <img
                src={item.img}
                alt={item.name}
                className="w-full h-64 object-cover"
              />
              <div className="absolute bottom-0 bg-black bg-opacity-50 w-full p-4 text-white">
                <h3 className="text-xl font-bold">{item.name}</h3>
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Testimonials */}
      <div className="py-20 bg-gray-800">
        <h2 className="text-4xl font-bold text-center text-white mb-10">
          What Our Guests Say
        </h2>
        <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-6">
          {[
            { quote: "The best stay ever!", name: "Aman, IIT Roorkee" },
            { quote: "Loved the food and ambiance!", name: "Priya, Delhi" },
            { quote: "Highly recommend Stayzo!", name: "Rohan, Mumbai" },
          ].map((testimonial, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.05 }}
              className="bg-gray-700 p-6 rounded-xl text-white shadow-lg"
            >
              <p className="text-lg italic">“{testimonial.quote}”</p>
              <p className="mt-4 text-right font-bold">{testimonial.name}</p>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default Home;
