import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import FoodCard from "../components/FoodCard";
import Cart from "../components/Cart";
import "./Food.css";

const Food = () => {
  const [foods, setFoods] = useState([]);
  const [cart, setCart] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  // Fetch menu items
  useEffect(() => {
    const fetchMenu = async () => {
      try {
        setIsLoading(true);
        const response = await fetch("/api/menu");

        if (!response.ok) {
          throw new Error("Failed to fetch menu");
        }

        const data = await response.json();
        setFoods(data);
      } catch (err) {
        setError(err.message);
        console.error("Error fetching menu:", err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchMenu();
  }, []);

  // Calculate cart total whenever cart changes
  useEffect(() => {
    const total = cart.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );
    setCartTotal(total);
  }, [cart]);

  // Add item to cart
  const addToCart = (item) => {
    setCart((prevCart) => {
      const existingItemIndex = prevCart.findIndex(
        (cartItem) => cartItem.id === item.id
      );

      if (existingItemIndex !== -1) {
        // Item exists, update quantity
        const updatedCart = [...prevCart];
        updatedCart[existingItemIndex] = {
          ...updatedCart[existingItemIndex],
          quantity: updatedCart[existingItemIndex].quantity + 1,
        };
        return updatedCart;
      } else {
        // Add new item
        return [...prevCart, { ...item, quantity: 1 }];
      }
    });
  };

  // Update item quantity
  const updateQuantity = (itemId, change) => {
    setCart((prevCart) => {
      const updatedCart = prevCart
        .map((item) => {
          if (item.id === itemId) {
            const newQuantity = item.quantity + change;
            return newQuantity > 0 ? { ...item, quantity: newQuantity } : null;
          }
          return item;
        })
        .filter(Boolean); // Remove null items (quantity <= 0)

      return updatedCart;
    });
  };

  // Remove item from cart
  const removeItem = (itemId) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== itemId));
  };

  // Proceed to checkout
  const proceedToCheckout = () => {
    if (cart.length === 0) return;

    // Store cart in localStorage or context/redux state
    localStorage.setItem("foodCart", JSON.stringify(cart));
    localStorage.setItem("cartTotal", cartTotal);

    // Navigate to Order page
    navigate("/order");
  };

  if (isLoading) return <div className="loading">Loading menu...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="food-page">
      <header className="food-header">
        <h1>Food Ordering System</h1>
      </header>

      <div className="food-content">
        <div className="menu-section">
          <h2>Our Menu</h2>
          {foods.length === 0 ? (
            <p>No food items available</p>
          ) : (
            <div className="food-grid">
              {foods.map((food) => (
                <FoodCard key={food._id} food={food} addToCart={addToCart} />
              ))}
            </div>
          )}
        </div>

        <div className="cart-section">
          <Cart
            cartItems={cart}
            updateQuantity={updateQuantity}
            removeItem={removeItem}
            total={cartTotal}
            proceedToCheckout={proceedToCheckout}
          />
        </div>
      </div>
    </div>
  );
};

export default Food;
