import React from "react";
import PropTypes from "prop-types";
import "./OrderSummary.css";

const OrderSummary = ({ order, proceedToPayment }) => {
  return (
    <div className="order-summary-container">
      <h2>Order Summary</h2>
      <div className="order-info">
        <p>
          <strong>Order ID:</strong> {order._id}
        </p>
        <p>
          <strong>Customer:</strong> {order.customer}
        </p>
        <p>
          <strong>Delivery Address:</strong> {order.address}
        </p>
        <p>
          <strong>Contact:</strong> {order.contactNumber}
        </p>
      </div>
      <h3>Order Items:</h3>
      <ul className="order-items">
        {order.items.map((item, index) => (
          <li key={index} className="order-item">
            <div className="item-info">
              <h4>{item.name}</h4>
              <p>Quantity: {item.quantity}</p>
            </div>
            <span className="item-total">${item.subtotal.toFixed(2)}</span>
          </li>
        ))}
        <li className="order-total">
          <strong>Total:</strong>
          <strong>${order.totalAmount.toFixed(2)}</strong>
        </li>
      </ul>
      <button className="payment-btn" onClick={proceedToPayment}>
        Proceed to Payment
      </button>
    </div>
  );
};

OrderSummary.propTypes = {
  order: PropTypes.object.isRequired,
  proceedToPayment: PropTypes.func.isRequired,
};

export default OrderSummary;
